package Repaso.Funciones.RepetirPalabra;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		// Crea una función que reciba una palabra y un número, y devuelva esa palabra repetida tantas veces como indique el número.
		
		repeticion("Mesa", 5);
	}
	
		
		
		public static void repeticion(String palabra, int numero) {
			
			for (int i = 0; i < numero; i++) {
				
			System.out.println(palabra);
			
		}
		
		
	}

}
